import { Component } from 'react'
import { arrayOf, shape, string, number, func } from 'prop-types';
import classes from './ContactList.module.css';

export default class ContactList extends Component {
    static propsTypes = {
        contacts: arrayOf(shape({
            id: string.isRequired,
            name: string.isRequired,
            number: number.isRequired
        })).isRequired,
        onDelete: func.isRequired
    }

    render() {
        const items = this.props.contacts;
        console.log('Чому цей компонент завантажується два рази ?');
        return (
            <ul className={classes.items}>
                {items && items.map(({name, number, id }) => (
                    <li
                        key={id}
                        className={classes.item}
                    >
                        {`${name}: ${number}`}
                        <button
                            className={classes.deleteBtn}
                            type="button"
                            onClick={()=>this.props.onDelete(id)}
                        >
                        <svg xmlns="http://www.w3.org/2000/svg"  viewBox="0 0 24 24" width="24px" height="24px">
                            <g id="surface61271541">
                             <path style=" stroke:none;fill-rule:nonzero;fill:rgb(92.54902%,94.117647%,94.509804%);fill-opacity:1;" d="M 10 2 L 9 3 L 3 3 L 3 5 L 4.109375 5 L 5.890625 20.253906 L 5.890625 20.261719 C 6.023438 21.25 6.878906 22 7.875 22 L 16.121094 22 C 17.117188 22 17.972656 21.25 18.105469 20.261719 L 18.109375 20.253906 L 19.890625 5 L 21 5 L 21 3 L 15 3 L 14 2 Z M 6.125 5 L 17.875 5 L 16.121094 20 L 7.875 20 Z M 6.125 5 "/>
                            </g>
                        </svg>
                        </button>
                    </li>
                ))}
            </ul>
        )
    }
}
