export const validateUsername = value => {
    const nameReg = /^[a-zA-Zа-яА-Я]+(([' -][a-zA-Zа-яА-Я ])?[a-zA-Zа-яА-Я]*)*$/;
    if (!nameReg.test(value)) {
        return 'Please Enter valid name '
    }
 }
